Bash(powershell -File - <<'SCRIPT'
$lines = Get-Content 'ab02ced4-caf6-4c6c-ad2c-07b7b817f79e.jsonl'
$count = 0
foreach \($line in $lines\) {
    if \($line -match '"type":"user"' -and $line -match '"content":"\([^"]+\)"'\) {
        $msg = $matches[1]
        if \($msg.Length -gt 50 -and $msg -notmatch 'tool_result'\) {
            Write-Host "=== MESSAGE $count ==="
            Write-Host $msg.Substring\(0, [Math]::Min\(500, $msg.Length\)\)
            Write-Host ""
            $count++
            if \($count -ge 20\) { break }
        }
    }
}
SCRIPT)
